import React from 'react';
import { useSelector } from 'react-redux';
import './App.css';
import Chat from './Chat';
import { selectUser } from './features/userSlice';
import Sidebar from './Sidebar';

function App() {
  const user = useSelector(selectUser);
  
  return (
    <div className="app">
      
      {/* Sidebar */}
      <Sidebar />
      {/* Chat */}
      <Chat />
    </div>
  );
}

export default App;
